import React, { useState, useEffect } from 'react';
import Post from './Post';
import axios from 'axios';


const DisplayPosts = () => {
    const mockPosts = {
        posts: [{
            title: "One",
            date: "31 June 2021",
            body: "Lorem ipsum blah blah blah something something"
        },
        {
            title: "Two",
            date: "31 June 2021",
            body: "Lorem ipsum blah blah blah something something"
        },
        {
            title: "Three",
            date: "31 June 2021",
            body: "Lorem ipsum blah blah blah something something"
        },
        {
            title: "Four",
            date: "31 June 2021",
            body: "Lorem ipsum blah blah blah something something"
        }],
        irrelevant: {
            body: "haha! I wasted your time!"
        }
    }



    const [posts, setPosts] = '';
    async function getPosts() {
        return await axios.get('http://localhost:3001/api/v1/posts/')
            .then((res) => {
                console.log(res);
                setPosts(res.data)
            })
            .catch(error => console.log(error));

    }
    if (mockPosts.posts.length > 0) {
        return (
            <div>
                {mockPosts.posts.map((post) => {
                    return (
                        <div>
                            <Post
                                date={post.date}
                                body={post.body}
                            />
                        </div>
                    )
                })}
            </div>
        )
    } else {
        return <h3>No posts to display yet.</h3>
    }
}

export default DisplayPosts;
