import React, { useState } from 'react';
import MomentUtils from '@date-io/moment';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import { Box, FormControl, Grid, InputLabel, MenuItem, Select, Typography } from '@material-ui/core';
import { MuiPickersUtilsProvider, KeyboardDatePicker } from '@material-ui/pickers';


const useStyles = makeStyles((theme) => ({
    root: {
        width: '100%',
        display: 'flex',
        justifyContent: 'center',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: '50%',
            height: theme.spacing(16),
        },
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
}));

const EntryForm = () => {
    const classes = useStyles();
    const [selectedDate, setSelectedDate] = useState(Date.now());
    const [feedbackType, setFeedbackType] = useState('');

    const handleDateChange = (date) => {
        setSelectedDate(date);
    }

    const handleFeedbackTypeChange = (type) => {
        setFeedbackType(type.target.value);
    };

    return (
        <form className={classes.root}>
            <Paper elevation={3}>
                <MuiPickersUtilsProvider utils={MomentUtils}>
                    <Grid
                        container
                        justify='space-around'
                    >
                        <KeyboardDatePicker
                            disableToolbar
                            variant='inline'
                            format='MM/DD/yyyy'
                            margin='normal'
                            label='feedback-date'
                            value={selectedDate}
                            onChange={handleDateChange}
                            KeyboardButtonProps={{
                                'aria-label': 'change date',
                            }}
                        />
                        <FormControl variant='outlined' className={classes.formControl}>
                            <InputLabel id='type-select-label'>Feedback Type</InputLabel>
                            <Select
                                labelId='type-select-label'
                                value={feedbackType}
                                onChange={handleFeedbackTypeChange}
                                label='Feedback Type'
                                >
                                    <MenuItem value=''>
                                        ----
                                    </MenuItem>
                                    <MenuItem value='praise'>Praise</MenuItem>
                                    <MenuItem value='praise'>Complaint</MenuItem>
                                    <MenuItem value='praise'>Peer Review</MenuItem>
                                    <MenuItem value='praise'>Comment</MenuItem>
                                    <MenuItem value='praise'>Request</MenuItem>
                                </Select>
                        </FormControl>
                    </Grid>
                </MuiPickersUtilsProvider>
            </Paper>
        </form>
    )
}

export default EntryForm;
